// DATASET: nationalParks from ./datasets/nationalParks

const nationalParksPrompts = {
  getParkVisitList() {
    /// Return an object containing the names of which parks I need to visit
    // and the ones I have already visited eg:
    // {
    //   parksToVisit: ["Yellowstone", "Glacier", "Everglades"],
    //   parksVisited: ["Rocky Mountain", "Acadia", "Zion"]
    //}
    let toVisit = [];
    let visited = [];
    const result = nationalParks.reduce((obj, item) => {
      if (item.visited === false) {
        toVisit.push(item.name);
      } else if (item.visited === true) {
        visited.push(item.name);
      }
      obj = {parksToVisit: toVisit, parksVisited: visited};
      return obj;
    }, {});
    return result;
    // Annotation:
    // Write your annotation here as a comment
  },

  getParkInEachState() {
    // Return an array of objects where the key is the state and the value is its National Park
    // eg: [ { Colorado: 'Rocky Mountain' },
    // { Wyoming: 'Yellowstone' },
    // { Montana: 'Glacier' },
    // { Maine: 'Acadia' },
    // { Utah: 'Zion' },
    // { Florida: 'Everglades' } ]
    const result = nationalParks.map(item => {
      return {
        [item.location]: item.name
      };
    });
    return result;
    // Annotation:
    // Write your annotation here as a comment
  },
  getParkActivities() {
    // Return an array of all the activities I can do
    // in a National Park. Make sure to exclude duplicates. eg:
    // [ 'hiking',
    //   'shoeshoing',
    //   'camping',
    //   'fishing',
    //   'boating',
    //   'watching wildlife',
    //   'cross-country skiing',
    //   'swimming',
    //   'bird watching',
    //   'canyoneering',
    //   'backpacking',
    //   'rock climbing' ]
    const result  = nationalParks.reduce((activities, activity) => {
      activity.activities.forEach(activity => {
        if (!activities.includes(activity)) {
          activities.push(activity);
        }
      });
      return activities;
    }, []);
    return result;
    // Annotation:
    // Write your annotation here as a comment
  }
};

